<div>* Evet arkadaslar sıra geldi MainActivity Java dosyamıza .</div>
<div></div>
<div>
<pre class="theme:eclipse lang:java decode:true">package com.example.sayfalararasigecisler;

import android.app.Activity;
import android.content.Intent;
import android.os.Bundle;
import android.view.View;
import android.view.View.OnClickListener;
import android.widget.Button;
import android.widget.Toast;

public class MainActivity extends Activity {

	@Override
	protected void onCreate(final Bundle savedInstanceState) {
		super.onCreate(savedInstanceState);
		setContentView(R.layout.main_activity);

		final Button btnDigerSayfa = (Button) findViewById(R.id.button1); // ( 1 )

		btnDigerSayfa.setOnClickListener(new OnClickListener() { // ( 2 )

			@Override
			public void onClick(final View v) {
				// TODO Auto-generated method stub
				Toast.makeText(getApplicationContext(),
						"Di�er Sayfaya Ge�iliyor..", Toast.LENGTH_SHORT).show(); // ( 3 )
				final Intent i = new Intent(MainActivity.this,
						IkinciSayfa.class); // ( 4 )
				startActivity(i); // ( 4 )
			}
		});

	}

}
</pre>
