<strong>IPerson</strong>
<pre class="theme:eclipse lang:java decode:true">import javax.servlet.ServletContext;
import java.sql.Connection;
import java.sql.SQLException;
import java.util.List;

/**
 * Created by wora on 07.11.2014.
 */
public interface IPerson {

    public Connection getConnection(ServletContext context);//get connection abstract method
    public void recordPerson(PersonBean person,Connection connection);//person record icin
    public List&lt;PersonBean&gt; getAllPerson(Connection connection);//tüm person datalari icin
    public void closeResource(Object object);//kaynaklari kapamatmak icin. Statement,ResultSet vs.


}
</pre>
<strong>JDBCPersonImpl.java</strong>
<pre class="theme:eclipse lang:java decode:true ">import javax.servlet.ServletContext;
import javax.servlet.ServletContextListener;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import java.util.ArrayList;
import java.util.List;
import java.util.Objects;

/**
 * Created by wora on 07.11.2014.
 */
public class JDBCPersonImpl implements IPerson {


    private static final String RECORD_PERSON = "INSERT INTO person(name, surname, gender) VALUES (?, ?, ?)";
    private static final String GET_ALL_PERSON = "SELECT * FROM person";
    private Connection connection ;


    @Override
    public Connection getConnection(ServletContext context){

        try{
            if (connection == null) {
                //context attribute da yer alan connection objemizi alıyoruz
                connection = (Connection)context.getAttribute("dbConnection");
            }
        }catch (Exception e){
            System.out.println("getConnection exception ");
            e.printStackTrace();
        }

        return connection;
    }

    @Override
    public  void recordPerson(PersonBean person,Connection connection){


        PreparedStatement statement = null;

        try{
            statement = connection.prepareStatement(RECORD_PERSON);
            statement.setString(1,person.getName());
            statement.setString(2,person.getSurname());
            statement.setString(3,person.getGender());

            statement.executeUpdate();
        }catch (SQLException e){
            System.out.println("recordPerson Exception");
            e.printStackTrace();
        }finally {
            closeResource(statement);
            closeResource(connection);
        }


    }

    @Override
    public ArrayList&lt;PersonBean&gt; getAllPerson(Connection connection) {

        System.out.println("getAllPerson method is started.");
        ArrayList&lt;PersonBean&gt; allPerson = new ArrayList&lt;PersonBean&gt;();
        PreparedStatement statement = null;
        ResultSet resultSet = null;

        try{
            statement = connection.prepareStatement(GET_ALL_PERSON);
            resultSet = statement.executeQuery();

            while (resultSet.next()){
                PersonBean person = new PersonBean();
                person.setName(resultSet.getString(1));
                person.setSurname(resultSet.getString(2));
                person.setGender(resultSet.getString(3));

                allPerson.add(person);
            }
        }catch (Exception e){
            System.out.println("GetAllPerson exception");
            e.printStackTrace();
        }finally {
            closeResource(resultSet);
            closeResource(statement);
        }

        System.out.println("getAllPerson method is finished.");
        return allPerson;
    }

    @Override
    public void closeResource(Object object) {
        try{
            if(object instanceof ResultSet){
                ((ResultSet)object).close();
            } else if(object instanceof PreparedStatement){
                ((PreparedStatement)object).close();
            }
        }catch (Exception e){
            System.out.println("Close resource exception");
        }

    }
}
</pre>
<strong>DBConnectListener.java</strong>
<pre class="theme:eclipse lang:java decode:true ">import javax.servlet.ServletContext;
import javax.servlet.ServletContextEvent;
import javax.servlet.ServletContextListener;
import java.sql.Connection;
import java.sql.DriverManager;
import java.util.Properties;

/**
 * Created by wora on 07.11.2014.
 */
public class DBConnectListener implements ServletContextListener {

    @Override
    public void contextInitialized(ServletContextEvent servletContextEvent) {
        /*Bu method servlet context olustugunda cagrilicaktır.
        Servlet context ise wep application deploy oldugunda olusur.*/
        System.out.println("> DbContextInitialized ~");

        final ServletContext context = servletContextEvent.getServletContext();
        final String driver = context.getInitParameter("driver");
        final String url = context.getInitParameter("url");
        final String username = context.getInitParameter("username");
        final String password = context.getInitParameter("password");

        try{
            Class.forName(driver);
            Connection connection = createConnection(url,username,password);
            context.setAttribute("dbConnection",connection);
        }catch (Exception e){
            e.printStackTrace();
            throw new RuntimeException("Could't not taken connection..");

        }

    }

    @Override
    public void contextDestroyed(ServletContextEvent servletContextEvent) {
      /*Bu method wep application undeploy oldugunda yada application server kapandiginda cagrilir.*/
        System.out.println("> DbContextDestroyed ~");
    }

    private Connection createConnection(final String url, final String username, final String password){
        System.out.println("createConnection method is started.");

        Connection connection = null;
        try{
            connection = DriverManager.getConnection(url,username,password);
        }catch (Exception e){
            throw new RuntimeException("createConnection exception : "+e.getMessage());
        }

        System.out.println("createConnection method is finished.");
        return connection;

    }
}</pre>
&nbsp;

&nbsp;

<strong>ServletRecord.java</strong>
<pre class="theme:eclipse lang:java decode:true ">import javax.servlet.ServletContext;
import javax.servlet.ServletException;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import java.io.IOException;
import java.sql.Connection;

/**
 * Created by wora on 07.11.2014.
 */
public class ServletRecord extends HttpServlet {

    protected void doPost(final HttpServletRequest request,final HttpServletResponse response)
            throws ServletException, IOException {

        //Kullanıcı bilgilerini parametre olarak alalim.
        final String name = request.getParameter("name");
        final String surname = request.getParameter("surname");
        final String gender = request.getParameter("gender");

        //Person nesnemizi olusturalim
        final PersonBean person = new PersonBean();
        person.setName(name);
        person.setSurname(surname);
        person.setGender(gender);

        final ServletContext context = getServletContext();//uygulamamızın context bilgisi
        final AppController appController = AppController.getInstance();//appcontrol objemiz
        final Connection connection = appController.jdbcPerson.getConnection(context);//connection objemizi controller objemiz üzerinden
        // IPerson interfacesini JDBC teknolojisi ile implemente eden
        // JDBCPersonImpl sınıfından alıyoruz.

        appController.jdbcPerson.recordPerson(person,connection);//person objemizi controller nesnemiz üzerinden
        // jdbc teknolojisi ile kayit ediyoruz.

        response.getWriter().println("Kayit edilen person : "+person.toString());
    }

}
</pre>
