<strong>GetAllPersonServlet </strong>
<pre class="theme:eclipse lang:java decode:true">import javax.servlet.ServletContext;
import javax.servlet.ServletException;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import java.io.IOException;
import java.io.PrintWriter;
import java.sql.Connection;
import java.util.ArrayList;

/**
 * Created by wora on 21.11.2014.
 */
public class GetAllPersonServlet extends HttpServlet {

    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {


        //get context
        final ServletContext context = getServletContext();
        final AppController controller = AppController.getInstance();//get app control singleton instance
        final Connection connection = controller.jdbcPerson.getConnection(context);// get jdbc connection
        final ArrayList&lt;PersonBean&gt; list =  controller.jdbcPerson.getAllPerson(connection);//get list person with jdbc technology

        final PrintWriter pw = response.getWriter();//get stream


        response.setContentType("text/html");//set response content type
        for (PersonBean bean : list){
            pw.println("Person : "+bean.toString()+"&lt;br&gt;");
        }

        pw.close();

    }
}
</pre>
