&lt;html&gt;
&lt;head&gt;
    &lt;title&gt;Html Hidden Form Fields&lt;/title&gt;
&lt;/head&gt;
&lt;body&gt;

    &lt;form action="http://localhost:8080/hiddenfields"&gt;
        Name : &lt;input type="text" name="user_name"&gt;
        &lt;input type="submit" value="Submit"&gt;
    &lt;/form&gt;

&lt;/body&gt;
&lt;/html&gt;
