<pre class="theme:eclipse lang:java decode:true">package wora.tutorial;

import bean.Person;
import com.sun.jersey.core.header.MediaTypes;

import javax.ws.rs.*;
import javax.ws.rs.core.MediaType;
import java.util.ArrayList;

/**
 * Created by ALİCAN on 22.4.2015.
 */
@Path("/api")
public class PersonRest {


    @GET
    @Produces("application/json")
    @Consumes("text/plain")
    @Path("/{id : [0-9]*}")
    public Person getPerson(@PathParam("id") int id){
       return new Person(id,"Alican","Akkus","male");
    }

    @GET
    @Produces("application/json")
    @Path("/")
    public ArrayList&lt;Person&gt; getPersons(){
        ArrayList&lt;Person&gt; list = new ArrayList&lt;Person&gt;();
        list.add(new Person(11,"alican","akkus","male"));
        list.add(new Person(22,"abdullah","sarikaya","male"));
        list.add(new Person(33,"ahmet","ates","male"));

        return list;
    }

    @POST
    @Produces("text/plain")
    @Consumes("application/json")
    @Path("/create")
    public String createPerson(Person person){
       return "Person is saved , person id : " + person.getId();
    }

    @PUT
    @Produces("application/json")
    @Consumes("application/json")
    @Path("/")
    public Person putPerson(Person person){
        person.setId(person.getId()+10);
        return person;
    }
}
</pre>
<span style="color: #ff6600;">bean</span>.<span style="color: #0000ff;">Person</span> classı;
<pre class="theme:eclipse lang:java decode:true ">package bean;

/**
 * Created by ALİCAN on 22.4.2015.
 */
public class Person {
    int id;
    String name;
    String surname;
    String gender;

    public Person() {
    }

    public Person(int id, String name, String surname, String gender) {
        this.id = id;
        this.name = name;
        this.surname = surname;
        this.gender = gender;
    }

    public int getId() {
        return id;
    }

    public void setId(int id) {
        this.id = id;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public String getSurname() {
        return surname;
    }

    public void setSurname(String surname) {
        this.surname = surname;
    }

    public String getGender() {
        return gender;
    }

    public void setGender(String gender) {
        this.gender = gender;
    }

}
</pre>
