<span style="color: #808000;">com.wora.servlet.<span style="color: #ff6600;">ContextListener</span></span>
<pre class="theme:eclipse lang:java decode:true ">package com.wora.servlet;

import java.io.File;
import java.io.FileInputStream;
import java.io.IOException;
import java.util.Properties;

import javax.servlet.ServletContextEvent;
import javax.servlet.ServletContextListener;

import com.wora.facade.ServiceFacade;

public class ContextListener implements ServletContextListener{

	public void contextDestroyed(ServletContextEvent event) {
		//TODO log
	}

	public void contextInitialized(ServletContextEvent event) {

		Properties appProps = null;
		try {
			appProps = new Properties();
			String appPropsPath = event.getServletContext().getRealPath("/WEB-INF/classes/RestfulTutorial.properties");


			File f = new File(appPropsPath);
			if (f.exists() &amp;&amp; !f.isDirectory()) {
				try {
					FileInputStream fis = new FileInputStream(f);
					appProps.load(fis);

					fis.close();
				} catch (IOException e) {
					//TODO log
				}
			} else {
				//TODO log
			}


			ServiceFacade.getInstance().start(appProps);
		} catch (Exception e) {
			//TODO log
		}

	}

}
